import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App() {
  return (
    <div className="container">
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}

function Pizza({ pizzaObject }) {
  return (
    <div className="pizzaStyle">
      <img src={pizzaObject.photoName} alt="Pizza" />
      <div>
        <h3>{pizzaObject.name}</h3>
        <p>{pizzaObject.ingredients}</p>
        <p>{pizzaObject.price} €</p>
        <p>{pizzaObject.soldOut ? "Sold out" : "Available"}</p>
      </div>
    </div>
  );
}

function Menu() {
  return (
    <main className="menu">
      <p>
        Authentic Itlian cuisine. 6 creative dishes to choose from. All from our
        stone oven, all organic, all delicious
      </p>
      <div className="pizzas">
        {pizzaData.map((pizza) => (
          <Pizza key={pizza.name} pizzaObject={pizza} />
        ))}
      </div>
    </main>
  );
}

function Header() {
  return (
    <header className="header">
      <h1>Pizzeria Sercolo</h1>
    </header>
  );
}

function Footer() {
  const hour = new Date().getHours();
  const openHour = 10;
  const closeHour = 22;
  const isOpen = hour >= openHour && hour < closeHour;

  return (
    <footer className="footer">
      {" "}
      {new Date().toLocaleDateString()} - © 2021 Pizzeria Sercolo
      <p>{isOpen ? "We are currently open" : "We are closed"}</p>
      <button className="btn">Order now</button>
    </footer>
  );
}

// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// React before 18
